module.exports = (() => {

    'use strict';

    const moment = require("moment-ferie-fr");

    class DateHandler {

        constructor(inputDate) {
            this._inDate = moment(inputDate);
            if (!this.isValid()) {
                throw new Error('Input date is invalid');
            }
            // Set the date to midnight for moment-ferie-fr
            this._inDate.hours(0).minutes(0).seconds(0).milliseconds(0);
        }

        isValid() {
          return this._inDate.isValid();
        }

        addDays(nb) {
            this._inDate.add(nb, 'days');
        }

        subDays(nb) {
            this._inDate.subtract(nb, 'days');
        }

        getDayInfos() {
            let payload = {};

            payload.dayNumber = this._inDate.day();
            payload.dayName = this._inDate.weekday();
            payload.timestamp = this._inDate.valueOf();
            payload.formatted = this._inDate.format('DD/MM/YYYY');

            // Checks if day is off and its celebration
            payload.isDayOff = false;
            if (this._inDate.isFerie()) {
                payload.isDayOff = true;
                payload.dayCelebration = this._inDate.getFerie();
            }

            return payload;
        }

    }

    return DateHandler;

})();
