const DateHandler = require('../date-handler');

module.exports = function(RED) {

    function dateInfosNode(config) {
        RED.nodes.createNode(this, config);

        this.addDays = config.addDays || 0;
        this.subDays = config.subDays || 0;

        this.on('input', (msg) => {
            try {
                const date = new DateHandler(msg.payload);
                if (this.addDays) {
                    date.addDays(this.addDays);
                }
                if (this.subDays) {
                    date.subDays(this.subDays);
                }
                msg.payload = date.getDayInfos();
            }
            catch (err) {
                this.warn(err.message);
            }
            this.send(msg);
        });
    }

    RED.nodes.registerType("date-infos", dateInfosNode);
};